import React, { useState, useCallback } from 'react';

const Student = ({ stu: { id, attributes: { name, age, gender, address } } }) => {
    // {stu:{id, attributes:{name, age, gender, address}}} = props  解构赋值 

    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)
    const delStu = useCallback(async () => {
        try {
            setLoading(true)
            setError(null)
            // 删除学生
            // http://localhost:1337/api/students/id
            const res = fetch(`http://localhost:1337/api/students/${id}`, {
                // fetch的第二个参数用来配置请求的详细信息
                method: 'delete'
            })
            // 判断是否成功
            if(!res.ok) {
                throw new Error('删除失败！')
            }

            // const data = await (await res).json() //被删除的学生
        } catch (error) {

        } finally {
            setLoading(false)
        }
    }, [])

    const deleteHandler = () => {

    }

    return (
        <tr>
            <td>{name}</td>
            <td>{gender}</td>
            <td>{age}</td>
            <td>{address}</td>
            <td><button onClick={deleteHandler}>删除</button></td>
        </tr>
    );
};

export default Student;
