import React from 'react';
import useSelector from 'react-redux'
import './store/index';

const App = () => {

    // useSelector() 用来加载state中的数据
    // useSelector(state => state)  获取所有的state
    useSelector(state => state.student) //属性名和store里定义的是一一对应的
    

    return (
        <div>
         App
        </div>
    );
};

export default App;
