import React from 'react';

const App = () => {
    console.log('App渲染');
    const [count, setCount] = useState(1);
    const clickHandler = () => {
        setCount(prev => prev + 1)
    }

    return (
        <div>
            <h2>App - {count}</h2>
            <button onClick={clickHandler}>增加</button>
        </div>
    );
}

export default App;
