// 使用RTK来构建store
// createSlice 创建reducer的切片
// 它需要一个配置对象作为参数，通过对象的不同的属性来指定它的配置
import {createSlice} from '@reduxjs/toolkit'

const stuSlice = createSlice({
    name: 'stu', // 用来自动生成action中的type  即case后面跟的常量
    initialState: {
        
    }    //当前切片的state的初始值

})
