import React from 'react';
import { useGetStudentsQuery } from './store/studentApi';

const App = () => {
    // 调用API查询数据
    // 这个钩子函数它会返回一个对象作为返回值，请求过程中的相关数据都会在该对象中存储
    const {data, isSuccess} = useGetStudentsQuery() //调用Api中的钩子查询数据
    return (
        <div>
            {/* useGetStudentsQuery是异步的，如果上面的data还没有回来，那么页面这里的data.data就会报错（undefined的属性读取） */}
            {/* 所以要加个判断,这个判断isSuccess就在返回的数据里 */}
            {data.data.map(item => <p key={item.key}>
                {item.attributes.name}---
                {item.attributes.age}---
                {item.attributes.gender}---
                {item.attributes.address}
                </p>)}
        </div>
    );
};

export default App;
