import React, { useState } from 'react';
import B from './B';

const App = () => {

    console.log('App组件重新渲染了！');

    const [count, setCount] = useState(0);

    // setTimeout(() => { setCount(1); }, 0) 也行
    /**
     * 
     */
    
    const clickHandler = () => {
        console.log('点击按钮！');
        setCount(1);
    };

    return (
        <div>
            {count}

            <B />
            <button onClick={clickHandler}>点我一下</button>
        </div>
    );
};

export default App;
