/**
 * Strapi 可以帮助我们快速搭建一个供我们使用的API服务器
 * 在server文件夹下
 */

import React, { useEffect, useState } from 'react';
import StudentList from "./components/StudentList";
import './App.css';


const App = () => {

    const [stuData, setStuData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null)

    // useEffect must not return anything besides a function, which is used for clean-up
    // useEffect不能传异步函数，所以传一个普通函数，里面定义了异步函数（多定义了一层）
    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true)
                // 用await async改写
                const res = await fetch('http://localhost:1337/api/students')
                // 判断请求是否加载成功
                if (res.ok) {
                    const data = await res.json()
                    setStuData(data.data)
                } else {
                    throw new Error('数据加载失败！') //因为这里是同步写法，所以用不了.catch, 只能用try catch
                }
            } catch (error) {
                setError(error)
            }finally {
                setLoading(false)
            }
        }
        fetchData()
    }, []);

    return (
        <div className="app">
            {/* 数据没在加载或没出错，就显示表格 */}
            {(!loading && !error) && <StudentList stus={stuData} />}
            {loading && <p>数据正在加载中。。。</p>}
            {/* 与运算：如果请求正常，那么error为null 则返回的是null,而null不会显示到页面上；如果error是个对象，返回的是第二个值即P标签 */}
            {error && <p>数据加载异常！</p>}
        </div>
    );
};

export default App;
