import React from 'react';
import {Prompt} from 'react-router-dom'

const MyForm = () => {
    return (
        <div>
             {/* 离开页面时 */}
            <Prompt message={"将要离开页面，确认吗？"}></Prompt>
            <h2>表单</h2>
           
            <input type="text" />
        </div>
    );
}

export default MyForm;
