import React from 'react';
import {Link, NavLink} from "react-router-dom";

const Menu = () => {
    return (
        <div>
          <ul>
              <li>
                  <Link to="/">主页</Link>
              </li>
              <li>
                  <Link to="/about">关于</Link>
              </li>
              <li>
                {/* 当链接被激活的时候，NavLink可以做一个不同的显示 */}
                  <NavLink to="/student/2" act>学生</NavLink>
              </li>
          </ul>
        </div>
    );
};

export default Menu;
