    return (
        <div>
            <Layout>
                <Routes>
                    <Route path={"/"} element={<HomePage></HomePage>}></Route>
                    <Route path={"/profile"} element={
                        <NeedAuth>
                            <ProfilePage></ProfilePage>
                        </NeedAuth>
                    }></Route>
                    <Route path={"/auth-form"} element={<AuthPage></AuthPage>}></Route>
                </Routes>
            </Layout>

        </div>
    );
}

export default App;
