import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from "react-router-dom";

const MainMenu = () => {
    const auth = useSelector(state => {log})
    console.log(auth);
    return (
        <header>
            <ul>
                <li>
                    <Link to={'/'}>首页</Link>
                </li>
                {
                    !auth.isLogged && <li>
                        <Link to={'/auth-form'}>登录/注册</Link>
                    </li>
                }
                {
                    auth.isLogged && <li>
                        <Link to={'/profile'}>{auth.user}</Link>
                    </li>
                }

            </ul>
        </header>
    );
}

export default MainMenu;
