import { createSlice } from "@reduxjs/toolkit";

export const authSlice = createSlice({
    name:'auth',
    // 初始值
    initialState:{
        isLogged:false,
        token:'',
        user:null
    },
    reducers:{
        login(state, action){
            state.isLogged = true,
            state.token = action.payload.token,
            state.user = action.payload.user
        },
        logout(state){
            state.isLogged = false,
            state.token = null
            state.user = null
        }
    }
})

export const {login, logout} = authSlice.actions
