import React from 'react';

const About = (props) => {
    const clickHandler = () => {
        props.history.replace({
            pathname:'/students/2',
            state:{name:'1111'} 
        })
    }
    return (
        <div>
            <div>
                <button onClick={clickHandler}>按钮</button>
                <h2>关于我们，其实是师徒4人</h2>
                <ul>
                    <li>孙悟空</li>
                    <li>猪八戒</li>
                    <li>沙和尚</li>
                    <li>唐僧</li>
                </ul>
                 <Route path="/about/hello">
                    <Hello></Hello>
                </Route>
            </div>
        </div>
    );
}

export default About;
