import React from 'react';
import { useSelector } from 'react-redux';
import { Link } from "react-router-dom";

const MainMenu = () => {
    const auth = useSelector(state => state.auth)
    return (
        <header>
            <ul>
                <li>
                    <Link to={'/'}>首页</Link>
                </li>
                {
                    auth.isLogged && <li>
                        <Link to={'/profile'}>用户信息</Link>
                    </li>
                }
                
                {
                    auth.isLogged && <li>
                        <Link to={'/profile'}>用户信息</Link>
                    </li>
                }

            </ul>
        </header>
    );
}

export default MainMenu;
