import React, { useMemo, useState } from 'react';
import Some from "./components/Some";

function sum(a, b) {
    console.log('函数执行了');
    return a + b;
}

const App = () => {
    const [count, setCount] = useState(1)
    // sum函数
    const result = sum(123, 456)
    return (
        <div>
            <h1>App</h1>
            <h2>result: {result}</h2>
            <h3>{count}</h3>
            {/* 每次点的时候，sum函数都会重新执行 */}
            <button onClick={() => setCount(prevstate => prevstate + 1)}>点我</button>
        </div>
    );
};

export default App;
